create table `modules` (`id` bigint unsigned not null auto_increment primary key, `name` varchar(191) not null, `is_active` tinyint(1) not null default '1', `created_at` timestamp null, `updated_at` timestamp null) default character set utf8mb4 collate 'utf8mb4_unicode_ci';

alter table `modules` add `route` varchar(191) null after `is_active`;
